-- Create tables for "test_db"
GRANT ALL PRIVILEGES ON DATABASE test_db TO test_user;

\c test_db;

CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;
CREATE TABLE "TestLevels" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" character varying(100) NOT NULL,
    "Description" character varying(1000) NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_TestLevels" PRIMARY KEY ("Id")
);

CREATE TABLE "TestRuns" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "ExecutedAt" timestamp with time zone NOT NULL,
    "Environment" character varying(200) NOT NULL,
    "Notes" character varying(4000) NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_TestRuns" PRIMARY KEY ("Id")
);

CREATE TABLE "TestCases" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Title" character varying(100) NOT NULL,
    "Description" character varying(2000) NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone NOT NULL,
    "TestLevelId" integer NOT NULL,
    CONSTRAINT "PK_TestCases" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_TestCases_TestLevels_TestLevelId" FOREIGN KEY ("TestLevelId") REFERENCES "TestLevels" ("Id") ON DELETE RESTRICT
);

CREATE TABLE "TestResults" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "ActualResult" text NOT NULL,
    "TestCaseId" integer NOT NULL,
    "TestRunId" integer NOT NULL,
    "Status" integer NOT NULL,
    "ExecutedAt" timestamp with time zone NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_TestResults" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_TestResults_TestCases_TestCaseId" FOREIGN KEY ("TestCaseId") REFERENCES "TestCases" ("Id") ON DELETE RESTRICT,
    CONSTRAINT "FK_TestResults_TestRuns_TestRunId" FOREIGN KEY ("TestRunId") REFERENCES "TestRuns" ("Id") ON DELETE RESTRICT
);

