VALUES (4, TIMESTAMPTZ '2025-11-21T00:00:00Z', 'Acceptance Level Testing', 'Acceptance', TIMESTAMPTZ '2025-11-21T00:00:00Z');

CREATE INDEX "IX_TestCases_TestLevelId" ON "TestCases" ("TestLevelId");

CREATE INDEX "IX_TestResults_TestCaseId" ON "TestResults" ("TestCaseId");

CREATE INDEX "IX_TestResults_TestRunId" ON "TestResults" ("TestRunId");

SELECT setval(
    pg_get_serial_sequence('"TestLevels"', 'Id'),
    GREATEST(
        (SELECT MAX("Id") FROM "TestLevels") + 1,
        nextval(pg_get_serial_sequence('"TestLevels"', 'Id'))),
    false);

INSERT INTO "__EFMigrationsHistory" ("MigrationId", "ProductVersion")
VALUES ('20251229214018_InitialCreate', '9.0.11');

COMMIT;

-- Create the 'request_db' database if it does not exist
CREATE DATABASE request_db OWNER test_user;
GRANT ALL PRIVILEGES ON DATABASE request_db TO test_user;

-- Create tables for "request_db".
\c request_db;

CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;
CREATE TABLE "ResultMasters" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_ResultMasters" PRIMARY KEY ("Id")
);

CREATE TABLE "StatusMasters" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_StatusMasters" PRIMARY KEY ("Id")
);

CREATE TABLE "Requests" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "DirectoryPath" text NOT NULL,
    "TestLevelId" integer NOT NULL,
    "StatusId" integer NOT NULL,
    "ResultId" integer NOT NULL,
    "RegisteredAt" timestamp with time zone NOT NULL,
    "UpdateAt" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_Requests" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Requests_ResultMasters_ResultId" FOREIGN KEY ("ResultId") REFERENCES "ResultMasters" ("Id") ON DELETE RESTRICT,
    CONSTRAINT "FK_Requests_StatusMasters_StatusId" FOREIGN KEY ("StatusId") REFERENCES "StatusMasters" ("Id") ON DELETE RESTRICT
);

