CREATE INDEX "IX_Requests_ResultId" ON "Requests" ("ResultId");

CREATE INDEX "IX_Requests_StatusId" ON "Requests" ("StatusId");

SELECT setval(
    pg_get_serial_sequence('"ResultMasters"', 'Id'),
    GREATEST(
        (SELECT MAX("Id") FROM "ResultMasters") + 1,
        nextval(pg_get_serial_sequence('"ResultMasters"', 'Id'))),
    false);
SELECT setval(
    pg_get_serial_sequence('"StatusMasters"', 'Id'),
    GREATEST(
        (SELECT MAX("Id") FROM "StatusMasters") + 1,
        nextval(pg_get_serial_sequence('"StatusMasters"', 'Id'))),
    false);

INSERT INTO "__EFMigrationsHistory" ("MigrationId", "ProductVersion")
VALUES ('20251231215744_InitialCreate', '9.0.11');

